document.addEventListener("DOMContentLoaded",function(){document.querySelectorAll("div pre").forEach(function(value){hljs.highlightBlock(value);});document.getElementById("nav-burger").addEventListener("click",function(){document.getElementById("mobile-nav").classList.toggle("nav-open")});document.querySelectorAll(".nav-collapse li a").forEach(function(value){value.addEventListener("click",function(){document.getElementById("mobile-nav").classList.remove("nav-open");});});});